        li.innerHTML = f.name;
        li.classList.add(['fli'])
        if (f.type === 'dir') {
            li.classList.add('dirli')
        }
        else if (f.type === 'file') {
            li.classList.add('fileli')
            li.addEventListener('click', async function () {
                console.log(`open ${f.name}: ${f.content}`, f);
            })
        }
        else if (f.type === 'repo') {
            li.classList.add('dirli')

        }
